import { Component } from '@angular/core';
import { WeatherService } from '../weather.service';

@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html',
  styleUrls: ['./weather.component.css']
})
export class WeatherComponent {

  name: string = 'tenkasi';
  weatherData: any;


  constructor(private weatherService: WeatherService) {}

  ngOnInit() {
    this.getWeather();
  }

  getWeather() {
    this.weatherService.getWeather(this.name)
      .subscribe(data => {
        this.weatherData = data;
        this.getTemperature(this.weatherData.results[0].latitude, this.weatherData.results[0].longitude);
      });
  }

  getTemperature(latitude: number, longitude: number) {
    this.weatherService.getTemperature(latitude, longitude)
      .subscribe(data => {
        this.weatherData.weather = data;
      });
  }
  search() {
    this.getWeather();
  }
  getWeatherDescription(weatherCode: number): string {
    switch (weatherCode) {
      case 0:
        return 'Clear sky';
      case 1:
        return 'Mainly clear';
      case 2:
        return 'partly cloudy';
      case 3:
        return 'overcast';
      case 45:
        return 'Fog';
      case 48:
        return 'depositing rime fog';
      case 51:
        return 'Drizzle: Light';
      case 53:
        return  'moderate';
      case 55:
        return 'dense intensity';
      case 56:
        return 'Freezing Drizzle: Light';
      case 57:
        return 'dense intensity';
      case 61:
        return 'Rain: Slight';
      case 63:
        return 'moderate';
      case 65:
        return 'heavy intensity';
      case 66:
        return 'Freezing Rain: Light';
      case 67:
        return 'heavy intensity';
      case 71:
        return 'Snow fall: Slight';
      case 73:
        return 'moderate';
      case 75:
        return 'heavy intensity';
      case 77:
        return 'Snow grains';
      case 80:
        return 'Rain showers: Slight';
      case 81:
        return 'moderate';
      case 82:
        return  'violent';
      case 85:
        return 'Snow showers slight';
      case 86:
        return 'heavy';
      case 95:
        return 'Thunderstorm: Slight or moderate';
      case 96:
        return 'Thunderstorm with slight';
      case 99:
        return 'heavy hail';
      default:
        return 'Unknown';
    }
  }
}
