import { Component } from '@angular/core';
import { WeatherService } from '../weather.service';

@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html',
  styleUrls: ['./weather.component.css']
})
export class WeatherComponent {
  latitude: number = 52.52;
  longitude: number = 13.41;
  weatherData: any;
  name:string='';

  constructor(private weatherService: WeatherService) {}

  search() {
    this.weatherService.getWeather(this.latitude, this.longitude).subscribe(data => {
      this.weatherData = data;
    });
  }
}
<div>
  <h2>Weather App</h2>
  <input type="text" name="cityQuery" placeholder="Enter a city name" [(ngModel)]="cityQuery">
  <button (click)="searchCity()">Search City</button>
</div>
<div *ngIf="cities.length > 0">
  <h3>Search Results:</h3>
  <ul>
    <li *ngFor="let city of cities" (click)="selectCity(city)">
      {{ city.name }} ({{ city.lat }}, {{ city.lon }})
    </li>
  </ul>
</div>
<div *ngIf="selectedCity">
  <h3>Weather for {{ selectedCity.name }}</h3>
  <p>Temperature: {{ weatherData.hourly.temperature_2m[0] }}°C</p>
</div>
