import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class WeatherService {

  constructor(private http: HttpClient) { }

  getWeather(name: string) {
    const url = `https://geocoding-api.open-meteo.com/v1/search?name=${name}&count=10&language=en&format=json`;
    console.log('API URL:', url);
    return this.http.get(url);
  }

  getTemperature(latitude: number, longitude: number) {
    const url = `"https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=13.41&current_weather=true&hourly=temperature_2m,relativehumidity_2m,windspeed_10m"`;
    console.log('API URL:', url);

    return this.http.get(url);

  }
}
