import { Component } from '@angular/core';
import { WeatherService } from '../weather.service';

@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html',
  styleUrls: ['./weather.component.css']
})
export class WeatherComponent {
   city!: string;
  weatherData: any;

  constructor(private weatherService: WeatherService) {}

  getWeather() {
    this.weatherService.getWeather(this.city)
    .subscribe(data=>{
      this.weatherData=data;
      console.log(data);
    }); 
  } 
}
<div>
  <h2>Weather App</h2>
  <label for="latitude">Latitude:</label>
  <input type="text" id="latitude" name="latitude" placeholder="Enter latitude" [(ngModel)]="latitude">

  <label for="longitude">Longitude:</label>
  <input type="text" id="longitude" name="longitude" placeholder="Enter longitude" [(ngModel)]="longitude">

  <button (click)="search()">Get Weather</button>
</div>
<div *ngIf="weatherData">
  <h3>Weather for Latitude: {{ latitude }}, Longitude: {{ longitude }}</h3>
  <p>Temperature: {{ weatherData.hourly.temperature_2m[0] }}°C</p>
</div>
