import { Component } from '@angular/core';
import { WeatherService } from '../weather.service';

@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html',
  styleUrls: ['./weather.component.css']
})
export class WeatherComponent {
  name: string = '';
  weatherData: any;

  constructor(private weatherService: WeatherService) {}

  getWeather() {
    this.weatherService.getGeocodingData(this.name).subscribe(geocodingData => {
      const latitude = geocodingData.results[0].latitude;
      const longitude = geocodingData.results[0].longitude;

      this.weatherService.getWeatherData(latitude, longitude).subscribe(weatherData => {
        console.log('Weather API Response:', weatherData);
        this.weatherData = weatherData;
      });
    });
  }
}
