import { Component ,Input} from '@angular/core';


@Component({
  selector: 'app-wether-forecast',
  templateUrl: './wether-forecast.component.html',
  styleUrls: ['./wether-forecast.component.css']
})
export class WetherForecastComponent {
  forecastDates : Date[] = [];



  @Input() weeklyTemperatureData: number[] = [];
  @Input() weatherData: any; 
  currentDate: Date = new Date();

  days: string[] = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

constructor() {
  this.calculateForecastDates();
}
  getCurrentDayName(index: number): string {
    const todayIndex = this.currentDate.getDay();
    const dayIndex = (todayIndex + index) % 7;
    return this.days[dayIndex];
  }

  calculateForecastDates(){
    const currentDate = new Date();
    for (let i = 0; i < 7; i++) {
      const nextDate = new Date(currentDate);
      nextDate.setDate(currentDate.getDate() + i);
      this.forecastDates.push(nextDate);
    }
  }
}



<div class="container">
    <button>
<div class="left-arrow">&#x2190;</div></button>
<div class="weekly-forecast">
<div class="forecast-box" *ngFor="let day of days; let i=index">
    <div class="date"><span>{{ forecastDates[i] | date }}</span></div>
    <div class="day">{{ getCurrentDayName(i) }}</div>
    <div class="temperature">
        Temperature: <span class="span" *ngIf="weatherData && weatherData.weather">{{ weeklyTemperatureData[i] }} °C</span>
        Humunity: <span class="span" *ngIf="weatherData && weatherData.weather">{{weatherData.weather.hourly.relativehumidity_2m[i]}} %</span>
    </div>

    </div>
</div>
<button><div class="right-arrow">&#x2192;</div></button>

</div>
