import { Component } from '@angular/core';
import { WeatherService } from '../weather.service';

@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html',
  styleUrls: ['./weather.component.css']
})
export class WeatherComponent {

  latitude: number = 52.52; // Default latitude
  longitude: number = 13.41; // Default longitude
  temperature: number | undefined;

  constructor(private weatherService: WeatherService) { }

  getTemperature() {
    this.weatherService.getTemperature(this.latitude, this.longitude)
      .subscribe(data => {
        if (data && data.hourly && data.hourly.temperature_2m && data.hourly.temperature_2m.length > 0) {
          this.temperature = data.hourly.temperature_2m[0].value;
        }
      });
  }
}
